import React              from 'react'
import { render }         from 'react-dom'
import { some }           from 'lodash'
import { loadOnServer }   from 'redux-connect'
import { AppContainer }   from 'react-hot-loader'
import Store              from './store'
import extendify          from 'custom-extend'

const merge = extendify({
  inPlace: false,
  isDeep: true,
  arrays: 'concat'
})

global.__PROTIUM__ = { store: null }

export default class Application {

  static defaults = {
    router: null,
    root: null,
    store: {},
    page: {
      doctype: '<!doctype html>',
      rootVar: '__STATE__',
      rootId: 'application'
    }
  };

  constructor(options = {}) {
    this.options = merge({}, Application.defaults, options)
    this.options.store.rootVar = this.options.page.rootVar
    this.router = this.options.router
    this.store = new Store(this.options.store)
    if (this.router) {
      this.history = this.router.createHistory()
    }
  }

  createStore(history, http) {
    let store = http ? null : __PROTIUM__.store

    if (history) {
      this.store.upgradeWithRouting(
        this.router.getReducers(), 
        this.router.getMiddleware(history)
      )
    }

    // Only hang onto the store globally if clientside
    if (!store) {
      store = this.store.finalize(http)
      if (!http) {
        __PROTIUM__.store = store
      }
    }
    
    if (__CLIENT__ && history) {
      this.router.registerStore(history, store)
    }

    return store
  }

  getComponent(store, renderProps, http) {
    let component = (this.router)
      ? this.router.getComponent(renderProps, http)
      : this.options.root

    if (!component) {
      throw new Error('Must initialize the application with a `router` or `root` property')
    }

    let provider = this.store.getWrappedComponent(store, component)

    if (this.options.hot && __CLIENT__) {
      return <AppContainer store={store}>
        {provider}
      </AppContainer>
    }

    return provider
  }

  render() {
    const mountNode = document.getElementById(this.options.page.rootId)
    const http = null

    if (this.router) {
      const history = this.router.createHistory()
      const store = this.createStore(history, http)
      return this.router.match(history, store, http, (error, redirectLocation, renderProps)=> {
        const component = this.getComponent(store, renderProps)
        render(component, mountNode)
      })
    }

    const store = this.createStore()
    const component = this.getComponent(store)
    render(component, mountNode)
  }

  resolve(store, renderProps, http) {
    const component = this.getComponent(store, renderProps, http)
    return loadOnServer({store, ...renderProps})
      .then(()=> {
        return {
          store,
          component,
          status: getStatus(renderProps) || 200
        }
      })
  }

}

function getStatus(renderProps) {
  if (some(renderProps.routes, r => r.notFound)) {
    return 404
  }
  return null
}

 import { createAction } from 'redux-actions'

const loadStart = createAction('@reduxAsyncConnect/BEGIN_GLOBAL_LOAD')

const loadEnd = createAction('@reduxAsyncConnect/END_GLOBAL_LOAD')

export default function asyncMiddleware({ dispatch }) {  

  const tracker = new Tracker(dispatch)

  return next => action => {

    if (action.error) {
      console.error(`Error: ${action.type}\n`, action.payload)
      return next(action)
    }

    if (!__CLIENT__) {
      return next(action)
    }

    if (isPromise(action.payload)) {
      dispatch(loadStart())
      tracker.enqueue(action.payload)
    }

    return next(action)
  }
}

class Tracker {
  
  promises = []

  get loading() {
    return !!this.promises.length
  }

  constructor(dispatch) {
    this.dispatch = dispatch
  }
  
  enqueue(promise) {
    this.promises.push(promise)
    promise.then(x => {
      this.dequeue(promise)
      return x
    })
    .catch(err => {
      this.dequeue(promise)
      return Promise.reject(err)
    })
  }
  
  dequeue(promise) {
    let index = this.promises.indexOf(promise)
    this.promises.splice(index, 1)
    if (!this.loading) {
      setTimeout(x => {
        this.dispatch(loadEnd())
      }, 0)
    }
  }
}

function isPromise(x) {
  return x && x.then
}

import { isFSA } from 'flux-standard-action'

function isFunction(fn) {
  return typeof fn === 'function';
}

export default function contextThunkMiddleware(buildContext, options, http) {
  return store => next => action => {
    const { req, res } = http || {}
    const { dispatch, getState } = store
    
    let context = buildContext(store, options, http)

    if (!context) {
      context = {}
    }

    if (isFunction(action)) {
      return action({ dispatch, getState, ...context });
    }

    if (isFSA(action) && isFunction(action.payload)) {
      return next({
        ...action,
        payload: action.payload({ dispatch, getState, ...context }),
      });
    }

    return next(action);
  }
}

import Webpack          from 'webpack'
import Path             from 'path'
import AssetsPlugin     from 'webpack-assets-plugin'
import CleanPlugin      from 'clean-webpack-plugin'
import nodeExternals    from 'webpack-node-externals'
import devMiddleware    from 'webpack-dev-middleware'
import hotMiddleware    from 'webpack-hot-middleware'
import extendify        from 'custom-extend'

const merge = extendify({
  inPlace: false,
  isDeep: true,
  arrays: 'concat'
})

const __PRODUCTION__      = (process.env.NODE_ENV === 'production')
const __DEVELOPMENT__     = !(__PRODUCTION__)

export default class DevTools {

  static baseConfig = {
    environment: [],
    define: {},

    output: {
      filename: '[name].js',
      path: 'public',
      publicPath: '/assets/',
    },

    commonsChunk: {},
    vendorLibs: [
      'flux-standard-action',
      'isomorphic-fetch',
      'lodash',
      'protium',
      'qs',
      'react',
      'react-cookie',
      'react-dom',
      'react-helmet',
      'react-hot-loader/patch',
      'react-redux',
      'react-router',
      'react-router-bootstrap',
      'react-router-redux',
      'react-router-scroll/lib/useScroll',
      'redux',
      'redux-actions',
      'redux-connect',
      'redux-promise',
    ],

    module: {
      preLoaders: [
        {
          test: /\.jsx?$/,
          loader: "source-map-loader"
        }
      ],
      loaders: [
        {
          test: /\.jsx?$/,
          loaders: ['babel'],
          exclude: /node_modules/
        }
      ]
    },

    devtool: 'source-map'
  }

  constructor(cwd) {
    this.cwd = Path.resolve(cwd)
  }

  serverConfig(entrypoint, options = {}) {

    const opts = merge({}, DevTools.baseConfig, options)

    const entry = Array.isArray(entrypoint) ? entrypoint : [entrypoint]

    const config = {}

    config.context = this.cwd
    config.devtool = opts.devtool

    config.entry = { server: entry }

    config.output = opts.output
    config.output.path = Path.resolve(config.context, 'public')
    config.output.libraryTarget = 'commonjs'

    config.module = opts.module
    config.externals = [nodeExternals()]

    config.target = 'node'
    config.node = {
      console: false,
      global: false,
      process: false,
      Buffer: false,
      __filename: false,
      __dirname: false,
      setImmediate: false
    }

    delete config.hot
    delete config.define

    return config
  }

  browserConfig(entrypoint, options = {}) {

    const opts = merge({}, DevTools.baseConfig, options)

    const entry = Array.isArray(entrypoint) ? entrypoint : [entrypoint]

    const config = {}

    config.context = this.cwd
    config.devtool = opts.devtool

    config.entry = { 
      client: entry,
      vendor: opts.vendorLibs
    }

    config.output = opts.output
    config.output.filename = '[name].js'
    config.output.library = '__APPLICATION__'
    config.output.libraryTarget = 'var'

    if (config.output.path === DevTools.baseConfig.output.path) {
      config.output.path = Path.resolve(config.context, 'public')
    }

    config.module = opts.module

    config.plugins = [
      new Webpack.optimize.CommonsChunkPlugin({
        name: 'vendor', 
        filename: 'vendor.js',
        async: false
      }),
      new AssetsPlugin({ 
        assetsRegex: /\.(jpe?g|png|gif|svg|scss|sass|css)$/i,
        metadata: true,
        prettyPrint: true,
        filename: 'assets.json'
      }),
      new Webpack.EnvironmentPlugin([
        'NODE_ENV',
        ...opts.environment
      ]),
      new Webpack.DefinePlugin({
        __CLIENT__: true,
        __SERVER__: false,
        __DEVELOPMENT__: __DEVELOPMENT__,
        __PRODUCTION__: __PRODUCTION__,
        ...opts.define
      }),
      ...opts.plugins || []
    ]

    if (__DEVELOPMENT__ && opts.hot) {
      config.entry.client.unshift(
        'webpack-hot-middleware/client',
        'webpack/hot/only-dev-server',
        'react-hot-loader/patch'
      )

      config.plugins.push(
        new Webpack.HotModuleReplacementPlugin(),
        new Webpack.IgnorePlugin(/webpack-stats\.json$/)
      )
    }

    return config
  }

  devMiddleware(config, options = {}) {
    let browserConfig, serverConfig

    if (Array.isArray(config)) {
      browserConfig = config[0]
      serverConfig = config[1]
    } else {
      browserConfig = config.browser
      serverConfig = config.server
    }

    // SERVER SIDE //
    const serverCompiler = Webpack(serverConfig)
    serverCompiler.watch({}, (err, stats)=> {
      if (err) {
        throw err
      }
      if (stats.hasWarnings() || stats.hasErrors()) {
        console.log(stats.toString(statsOptions))
      } else {
        let s = stats.toJson()
        console.log(`(server) webpack built ${s.hash} in ${s.time}ms`)
      }
    })


    // CLIENT SIDE //
    const devCompiler = Webpack(browserConfig)
    const devDefaultOptions = {
      quiet: true,
      noInfo: true,
      hot: true,
      inline: true,
      lazy: false,
      publicPath: browserConfig.output.publicPath,
      headers: {'Access-Control-Allow-Origin': '*'},
      stats: {colors: true}
    }

    const devOptions = {...devDefaultOptions, ...(options.middleware || {})}

    const statsDefaults = {
      hash: false,
      version: false,
      timings: true,
      assets: true,
      chunks: false,
      chunkModules: false,
      modules: false,
      colors: true
    }

    const statsOptions = {...statsDefaults, ...(options.stats || {})}

    return [
      devMiddleware(devCompiler, devOptions),
      hotMiddleware(devCompiler)
    ]
  }

}

import React, {Component} from 'react'
import Helmet from 'react-helmet'
import PrettyError from 'pretty-error'
import AnsiToHTML from 'ansi-to-html'

const pe = new PrettyError()
const convert = new AnsiToHTML();
pe._renderer._config.terminalWidth = 9999

export default class ErrorComponent extends Component {
  render() {
    const { error, store, production } = this.props
    let pError = pe.render(error)

    // Ridiculous whitespace hack
    const errString = (convert.toHtml(pError))
      .replace(/<\/span>    <span style="color:#AAA">/gm, '')

    const errStyle = {
      color: '#fff',
      backgroundColor: '#000',
      whiteSpace: 'pre-line',
      overflow: 'scroll',
      fontFamily: 'monospace',
      padding: '15px'
    }

    return <div className="container">
      <Helmet title="Error" />
      <span className="label label-danger">
        <strong>ERROR</strong>
      </span>
      <h1>{error.message}</h1>
      {!production && <div>
        <h3>Stack Trace</h3>
        <div style={errStyle} 
             className="well" 
             dangerouslySetInnerHTML={{__html: errString}}></div>
      </div>}
      {!production && store && <div>
        <h3>State</h3>
        <pre>{JSON.stringify(store.getState(), null, 2)}</pre>
      </div>}
    </div>
  }
  
  static inlineCss = `
    html, body { margin: 0; padding 0; }
    * { box-sizing: border-box; }
    body {
      background: #e8e8e8;
      color: #333;
      font-family: sans-serif;
      padding: 30px;
      margin: 0;
    }
    
    #application {
      max-width: 90%;
      padding: 30px;
      margin: 0 auto;
      background: #fff;
      border-radius: 4px;
    }
    .label {
      text-transform: uppercase;
      color: #999;
    }
    h1 {
      font-size: 25px;
      letter-spacing: -0.04em;
      border-bottom: 1px solid #d0d0d0;
      padding-bottom: 0.5em;
      margin-bottom: 1em;
    }
    pre {
      background: #e8e8e8;
      overflow:scroll;
      padding: 12px 15px;
      border: 1px solid #d0d0d0;
      border-radius: 4px;
      line-height: 1.3;
    }
`
}

import 'isomorphic-fetch'
import Url        from 'url'
import QS         from 'qs'
import { map }    from 'lodash'
import extendify  from 'custom-extend'

const merge = extendify({
  inPlace: false,
  isDeep: true,
  arrays: 'concat'
})

const methods = ['get', 'post', 'put', 'patch', 'delete', 'del']

export default class FetchClient {

  static defaults = {
    server: {
      protocol: 'http',
      host: 'localhost',
      pathname: '/',
      port: process.env.API_PORT || process.env.PORT || 5000
    },
    client: {
      pathname: '/'
    }
  };

  static requestDefaults = {
    method        : 'get',
    mode          : 'same-origin',
    credentials   : 'same-origin',
    redirect      : 'follow',
    cache         : 'default',
    as            : 'json',
    type          : 'json',
    headers       : {}
  }

  constructor(options, store, http) {
    if (http) {
      this.setHttp(http)
    }

    this.store = store
    this.options = merge({}, FetchClient.defaults, options)
    
    methods.forEach(method => {
      this[method] = this.genericMethod.bind(this, method)
    })
  }

  setHTTP(http) {
    delete this.req
    delete this.res
    this.req = http.req
    this.res = http.res
  }

  isExternal(path) {
    return /^https?:\/\//i.test(path)
  }

  buildOptions(method, path, opts) {
    let options = merge({}, FetchClient.requestDefaults, opts)
    let external = this.isExternal(path)

    options.url = this.formatUrl(path)
    
    options.method = method.toUpperCase()

    if (options.method === 'DEL') {
      options.method = 'DELETE'
    }

    if (options.query) {
      options.url += ('?' + QS.stringify(options.query))
      delete options.query
    }

    if (options.headers) {
      if (__SERVER__ && this.req.get('cookie')) {
        options.headers.cookie = this.req.get('cookie')
      }

      if (this.options.auth && typeof this.options.auth.getBearer === 'function') {
        try {
          let token = this.options.auth.getBearer(this.store)
          if (token && token.length) {
            options.headers['Authorization'] = `Bearer ${token}`
          }
        } catch(e) {
          console.log('FetchClient@buildOptions: Unable to retrieve token', e, e.stack)
        }
      }

      options.headers = new Headers(options.headers)
    }

    if (options.data && !options.body) {
      options.body = options.data
    }

    if (options.body && typeof options.body !== 'string' && options.type.toLowerCase() === 'json') {
      options.body = JSON.stringify(options.body)
      options.headers.set('Content-Type', 'application/json')
    }
    
    if (external) {
      options.mode = 'cors'
    }

    return options
  }

  genericMethod(method, path, opts = {}) {
    let req = this.req
    let options = this.buildOptions(method, path, opts)
    let url = ''+options.url
    delete options.url
    let request = new Request(url, options)

    return fetch(request).then(response => {
      if (!response.ok) {
        return Promise.reject(response)
      }

      switch(options.as) {
        case 'json':
          return response.json()
        case 'text':
          return response.text()
        case 'blob':
          return response.blob()
        default:
          return response
      }
    })
  }

  formatUrl(path) {
    if (this.isExternal(path)) {
      return path
    }

    // Copy config to avoid mutating options
    let config = __SERVER__ 
      ? { ...this.options.server }
      : { ...this.options.client }

    const adjustedPath = path[0] === '/' ? path.slice(1) : path

    if (config.base) {
      config.pathname = config.base
    }

    if (config.host && config.port) {
      config.host = `${config.host}:${config.port}`
    }

    const baseUrl = Url.format(config)

    return baseUrl + adjustedPath
  }
}

import React, { Component }   from 'react'
import { renderToString }     from 'react-dom/server'
import { map }                from 'lodash'
import serialize              from 'serialize-javascript'
import Helmet                 from 'react-helmet'

export default class HtmlPage extends Component {

  static defaultProps = {
    state: {},
    doctype: '<!doctype html>',
    component: <div/>,
    rootVar: '__STATE__',
    rootId: 'application'
  };

  render() {
    const html = this.renderAppHtml()
    const head = this.getHead()
    let htmlAttributes = head.htmlAttributes.toComponent()

    // Hack to avoid React createElement warning
    // https://github.com/nfl/react-helmet/issues/150
    htmlAttributes = (Array.isArray(htmlAttributes) && !htmlAttributes.length) 
      ? {} 
      : htmlAttributes

    return <html {...htmlAttributes}>
      <head>
        {head.title.toComponent()}
        {head.meta.toComponent()}
        {this.renderInlineCss()}
        {this.renderAsyncLinks()}
        {this.renderInlineScripts()}
        {head.link.toComponent()}
      </head>
      <body>
        {html}
        {this.renderState()}
        {this.renderMain()}
        {head.script.toComponent()}
      </body>
    </html>
  }

  getHead() {
    return Helmet.rewind()
  }

  asyncCss() {
    return this.props.inlineCss && this.props.inlineCss.length
  }

  renderMain() {
    if (!this.props.main) { return null }
    let scripts = Array.isArray(this.props.main)
          ? this.props.main
          : [this.props.main]
    return map(scripts, (script, i)=> {
      return this.renderScriptTag(script, i)
    })
  }

  renderScriptTag(src, key) {
    if (!src) return <noscript />
    return <script key={key} src={src} />
  }

  renderAsyncLinks() {
    if (!this.props.asyncLinks) { return null }
    let calls = map(this.props.links, k => `loadCSS("${k}")`).join(';')
    let html = LOADCSS_SCRIPT + calls
    return <script dangerouslySetInnerHTML={__(html)} />
  }

  renderInlineScripts() {
    if (!this.props.inlineScripts) { return null }
    let scripts = Array.isArray(this.props.inlineScripts) 
          ? this.props.inlineScripts 
          : [this.props.inlineScripts]
    return map(scripts, (script, i)=> {
      let html = __(script)
      return <script key={'inline-script-'+i} dangerouslySetInnerHTML={html} />
    })
  }

  renderInlineCss() {
    if (!this.props.inlineCss) { return null }
    let links = Array.isArray(this.props.inlineCss) 
          ? this.props.inlineCss 
          : [this.props.inlineCss]
    return map(links, (css, i)=> {
      let html = __(css)
      return <style key={'inline-css-'+i} dangerouslySetInnerHTML={html} />
    })
  }

  renderAppHtml() {
    const html = renderToString(this.props.component)
    return <div id={this.props.rootId} dangerouslySetInnerHTML={__(html)} />
  }

  renderState() {
    let html = `${this.props.rootVar}=${serialize(this.props.state)}`
    return <script dangerouslySetInnerHTML={__(html)} />
  }
}

const LOADCSS_SCRIPT = `
(function(c){c.loadCSS=function(h,e,k){function f(a){for(var d=b.href,c=g.length;c--;)
if(g[c].href===d)return a();setTimeout(function(){f(a)})}var d=c.document,
b=d.createElement("link"),a;e?a=e:(a=(d.body||d.getElementsByTagName("head")[0]).childNodes,
a=a[a.length-1]);var g=d.styleSheets;b.rel="stylesheet";b.href=h;b.media="only x";
a.parentNode.insertBefore(b,e?a:a.nextSibling);b.c=f;f(function(){b.media=k||"all"});
return b};"undefined"!==typeof module&&(module.b=c.loadCSS)})(this);
`.replace(/\n/g, '')

function __(html) {
  return { __html: html }
}

// import 'source-map-support/register'
import React                      from 'react'
import path                       from 'path'
import extendify                  from 'custom-extend'
import _require                   from 'webpack-external-require'
import { plugToRequest }          from 'react-cookie'
import HtmlPage                   from './htmlpage'
import ErrorComponent             from './error'
import Application                from './application'
import { 
  renderToString, 
  renderToStaticMarkup 
} from 'react-dom/server'

const merge = extendify({
  inPlace: false,
  isDeep: true,
  arrays: 'concat'
})

const DOCTYPE = '<!doctype html>'

const production = process.env.NODE_ENV === 'production'

global.__SERVER__ = true
global.__CLIENT__ = false

export function renderer(appFn, options = {}, contextualOptions = ()=> ({})) {

  return (req, res)=> {
    if (req.url === '/favicon.ico') {
      return res.sendStatus(404)
    }

    let app;

    try {
      if (typeof appFn === 'string') {
        let appPath = _require.resolve(appFn)
        if (!__PRODUCTION__) {
          delete _require.cache[appPath]
        }
        let _app = _require(appPath)
        app = _app.default ? _app.default : _app
      } 
      else if (typeof appFn === 'function') {
        app = appFn()
      } 
      else if (appFn instanceof Application) {
        app = appFn
      } 
      else {
        throw new Error('Unable to load application')
      }
    } catch(err) {
      const page = getErrorPage(null, null, err)
      return res.status(500).send(getHtml(null, page))
    }


    try {

    const http = {req, res}
    const unplug = plugToRequest(req, res)
    const history = app.router.createHistory()
    const store = app.createStore(history, http)

    const appOptions = merge({}, options, contextualOptions(req, res))

    return app.router.match(history, store, http, (error, redirect, renderProps) => {
      if (error) {
        const page = getErrorPage(null, app, error)
        return res.status(500).send(getHtml(app, page))
      }

      if (redirect) {
        return res.redirect(302, redirect.pathname + redirect.search)
      }

      if (!renderProps) {
        return res.status(404)
      }

      return app.resolve(store, renderProps, http)
        .then(({ store, component, status })=> {
          const page = getHtmlPage(store, app, component, appOptions.page)
          res.status(status).send(getHtml(app, page))
        })
        .catch((err)=> {
          const page = getErrorPage(null, app, err)
          res.status(500).send(getHtml(app, page))
        })
        .catch((err)=> {
          const page = getErrorPage(null, null, err)
          res.status(500).send(getHtml(null, page))
        })
        .then(unplug)
    })

    } catch(err) {
      const page = getErrorPage(null, null, err)
      res.status(500).send(getHtml(null, page))
      try { unplug() } catch(e) {}
      return
    }

    // Should never fire
    res.sendStatus(500)
  }
}

function getHtml(app, page) {
  let dtype = DOCTYPE
  if (app) {
    dtype = app.options.page.doctype
  }
  return dtype + renderToString(page)
}

function getErrorPage(store, app, error, extraOpts = {}) {
  let options = app && app.options ? merge({}, app.options.page, extraOpts) : extraOpts
  let ErrComp = ErrorComponent
  let inlineCss = ErrComp.inlineCss

  if (options.page) {
    options.page.main = false

    if (options.page.errorComponent) {
      ErrComp = options.page.errorComponent
    }

    if (!options.page.inlineCss) {
      options.page.inlineCss = ErrComp.inlineCss
    }
  }

  const comp = <ErrComp {...{store, app, error, production}} />
  const opts = !app ? {inlineCss} : null
  return getHtmlPage(store, app, comp, opts)
}

function getHtmlPage(store, app, component, extraOpts) {
  let options = app ? merge({}, app.options.page, extraOpts) : extraOpts
  let RootComp = HtmlPage
 
  options.component = component
 
  if (store) {
    options.state = store.getState()
  }

  if (app && app.options.page && app.options.page.rootComponent) {
    RootComp = app.options.page.rootComponent
  }

  return <RootComp {...options} />
}

